/**
 * Parsuje zawartość pliku i zwraca listę poprawnych 8-cyfrowych kodów rozliczeniowych.
 * @param {string} fileContent Zawartość pliku plewibnra.txt.
 * @returns {Array<string>} Tablica poprawnych 8-cyfrowych kodów.
 */
function parsePlewiNrbCodes(fileContent) {
    const lines = fileContent.split('\n');
    const codes = new Set();
    const regex = /\t(\d{8})\t/; // Doprecyzowany regex, aby szukać dokładnie 8 cyfr

    for (const line of lines) {
        const match = line.match(regex);
        if (match) {
            codes.add(match[1]);
        }
    }

    return Array.from(codes);
}

const validNrbCodes = parsePlewiNrbCodes(plewibnraContent);

/**
 * Oblicza cyfrę kontrolną dla numeru rachunku bankowego (NRB)
 * zgodnie ze standardem IBAN (ISO 7064 Modulo 97-10).
 * @param {string} bban Numer rachunku bez cyfry kontrolnej.
 * @returns {string} Dwucyfrowa, poprawna cyfra kontrolna.
 */
function calculateNrbChecksum(bban) {
    const countryCode = '2521'; // PL = 2521 (P=25, L=21)
    const numberToCheck = bban + countryCode + '00';
    
    let remainder = '';
    let block;

    for (let i = 0; i < numberToCheck.length; i += 7) {
        block = remainder + numberToCheck.substring(i, i + 7);
        remainder = (parseInt(block, 10) % 97).toString();
    }
    
    const controlNumber = 98 - parseInt(remainder, 10);
    return String(controlNumber).padStart(2, '0');
}

/**
 * Generuje poprawny numer rachunku bankowego (NRB) z istniejącym kodem banku/oddziału.
 * @param {string} selectedBankCode Kod banku wybrany przez użytkownika.
 * @param {string} format 'compact' lub 'spaced'.
 * @param {string} prefix 'no-prefix' lub 'with-prefix'.
 * @returns {string} Sformatowany numer rachunku bankowego.
 */
function generateNrb(selectedBankCode, format, prefix) {
    let bankAndBranchCode;
    const digits = '0123456789';

    if (selectedBankCode === 'random') {
        if (validNrbCodes.length === 0) {
            return "Brak poprawnych kodów do wygenerowania. Sprawdź plik.";
        }
        // Losowo wybieramy poprawny 8-cyfrowy kod rozliczeniowy z listy
        bankAndBranchCode = validNrbCodes[Math.floor(Math.random() * validNrbCodes.length)];
    } else {
        // Filtrujemy listę, aby znaleźć kody pasujące do wybranego banku
        const filteredCodes = validNrbCodes.filter(code => code.startsWith(selectedBankCode));
        if (filteredCodes.length === 0) {
            return "Brak poprawnych kodów dla wybranego banku.";
        }
        bankAndBranchCode = filteredCodes[Math.floor(Math.random() * filteredCodes.length)];
    }

    // Generujemy 16 losowych cyfr dla numeru rachunku klienta
    let customerNumber = '';
    for (let i = 0; i < 16; i++) {
        customerNumber += digits.charAt(Math.floor(Math.random() * digits.length));
    }
    
    // Łączymy wszystkie części w poprawnej kolejności, bez sumy kontrolnej
    const fullNumberWithoutChecksum = bankAndBranchCode + customerNumber;

    // Obliczamy sumę kontrolną
    const checksum = calculateNrbChecksum(fullNumberWithoutChecksum);

    // Tworzymy pełny numer NRB z sumą kontrolną na początku
    let finalNrb = `${checksum}${fullNumberWithoutChecksum}`;

    // Stosujemy formatowanie
    if (format === 'spaced') {
        finalNrb = `${finalNrb.substring(0, 2)} ${finalNrb.substring(2, 6)} ${finalNrb.substring(6, 10)} ${finalNrb.substring(10, 14)} ${finalNrb.substring(14, 18)} ${finalNrb.substring(18, 22)} ${finalNrb.substring(22, 26)}`;
    }

    // Dodajemy prefiks IBAN
    if (prefix === 'with-prefix') {
        // KLUCZOWA ZMIANA: Dodajemy spację po prefiksie, jeśli format to 'spaced'
        if (format === 'spaced') {
            finalNrb = `PL ${finalNrb}`;
        } else {
            finalNrb = `PL${finalNrb}`;
        }
    }

    return finalNrb;
}


